mapper.select:
SELECT 
	,
	ro.area_id AS "areaList.id"
FROM sys_role a
LEFT JOIN sys_area o ON o.id = a.area_id
LEFT JOIN sys_user_role ur ON ur.role_id = a.id
LEFT JOIN sys_user u ON u.id = ur.user_id
LEFT JOIN sys_role_area ro ON ro.role_id = a.id
WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.useable=#{useable}

	AND u.id = #{user.id}


	AND u.login_name = #{user.loginName}


${sqlMap.dsf}
ORDER BY o.code, a.name

mapper.insert:
INSERT INTO sys_role_menu(role_id, menu_id)

	SELECT #{id}, #{menu.id}

	FROM dual

INSERT INTO sys_role_area(role_id, area_id)

	SELECT #{id}, #{area.id}

	FROM dual

mapper.update:
UPDATE sys_role SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
