mapper.select:
SELECT 

FROM real_${tableName} r

WHERE r.parameterId = ${parameterId} 

AND r.time BETWEEN ${endTime} AND ${startTime}

SELECT 

FROM real_${tableName} r

WHERE r.parameterId = ${parameterId} 

AND r.time BETWEEN ${endTime} AND ${startTime}

SELECT r.parameterId, "${tableName}" AS "tableName", AVG(r.value) AS "value" FROM real_${tableName} r
WHERE r.parameterId = ${parameterId}
AND r.time BETWEEN "${endTime}" AND "${startTime}" GROUP BY r.parameterId
