mapper.select:
SELECT 

FROM real_#{tableName} r

WHERE r.parameterId = #{parameterId} 

AND r.time BETTWEN #{endTime} AND #{startTime}

SELECT 

FROM real_#{tableName}

WHERE parameterId = #{parameterId} 

AND time BETTWEN #{endTime} AND #{startTime}

SELECT 
    r.id,
    r.parameterId,
    #{tableName} AS "tableName",
    AVG(r.value) AS "value"
FROM real_#{tableName}

WHERE parameterId = #{parameterId} 

AND time BETTWEN #{endTime} AND #{startTime}
